pragma solidity >=0.6.2 <0.8.0;

library LibPart {
    bytes32 public constant TYPE_HASH = keccak256("Part(address account,uint96 value)");
    bytes32 public constant ARRAY_TYPE_HASH = keccak256("Parts(Part[] parts)Part(address account,uint96 value)");

    struct Part {
        address payable account;
        uint96 value;
    }

    function hash(Part memory part) internal pure returns (bytes32) {
        return keccak256(abi.encode(TYPE_HASH, part.account, part.value));
    }

    function hashParts(Part[] memory parts) internal pure returns (bytes32) {
        bytes32[] memory partsBytes = new bytes32[](parts.length);
        for (uint256 i = 0; i < parts.length; i++) {
            partsBytes[i] = LibPart.hash(parts[i]);
        }
        return keccak256(abi.encode(ARRAY_TYPE_HASH, keccak256(abi.encodePacked(partsBytes))));
    }

}
"},"contracts/exchange/IGlipERC721LazyData.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.7.6;
pragma abicoder v2;

import "./../tokens/@rarible/royalties/contracts/LibPart.sol";

interface IGlipERC721LazyData {

    struct DecodedMintData {
        uint256 tokenId; // Token id
        uint256 reserve; // Reserve
        address creator; // Primary creator
        LibPart.Part[] payouts; // Payout split %s
        LibPart.Part minter; // Minter fee %
        LibPart.Part royalty;
    }

}
"},"contracts/exchange/IGlipERC721Lazy.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.7.6;
pragma abicoder v2;

import "./../tokens/@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "./../tokens/@rarible/royalties/contracts/LibPart.sol";
import "./IGlipERC721LazyData.sol";

interface IGlipERC721Lazy is IGlipERC721LazyData, IERC721Upgradeable {

    function decodeLazyMintData(bytes calldata encoded) external view returns(DecodedMintData memory);

    function mintAndTransferEncodedData(bytes memory encoded, address to) external;

    function transferOwnership(address newOwner) external;

    function owner() external view returns (address);

}
"},"contracts/tokens/@openzeppelin/contracts-upgradeable/token/ERC721/IERC721ReceiverUpgradeable.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity >=0.6.0 <0.8.0;

/**
 * @title ERC721 token receiver interface
 * @dev Interface for any contract that wants to support safeTransfers
 * from ERC721 asset contracts.
 */
interface IERC721ReceiverUpgradeable {
