        address taker;
        address token;
        address auctioneer;
        uint tokenId;
        uint value;
        uint start;
        uint end;
        bytes tokenData;
        bytes signature;
    }

    function hash(Bid memory bid) internal pure returns (bytes32) {

        bytes32 hashStruct = keccak256(abi.encode(
            BID_TYPEHASH,
            bid.maker,
            bid.taker,
            bid.token,
            bid.auctioneer,
            bid.tokenId,
            bid.value,
            bid.start,
            bid.end,
            keccak256(bid.tokenData)
