pragma solidity 0.7.6;
pragma abicoder v2;

import "../erc-1155/ERC1155OpenGlip.sol";
import "./../@openzeppelin/contracts/proxy/UpgradeableBeacon.sol";
import "./../@openzeppelin/contracts/proxy/BeaconProxy.sol";
import "./../@openzeppelin/contracts/access/Ownable.sol";

/**
 * @dev This contract is for creating proxy to access ERC1155OpenGlip token.
 *
 * The beacon is initialized in the constructor.
 *
 */
contract ERC1155OpenGlipFactoryC2 is Ownable {
    address public beacon;
    address exchangeProxy;
    address defaultMinter;
    address forwarder;

    event ERC1155OpenGlipProxy(address proxy, address indexed owner);

    constructor(address _beacon, address _exchangeProxy, address _defaultMinter, address _forwarder) {
        beacon = address(_beacon);
        updateExchangeMinterForwarder(_exchangeProxy, _defaultMinter, _forwarder);
    }

    function updateExchangeMinterForwarder(address _exchangeProxy, address _defaultMinter, address _forwarder ) public onlyOwner {
        exchangeProxy = _exchangeProxy;
        defaultMinter = _defaultMinter;
        forwarder = _forwarder;
    }

    function create(address to, string memory _name, string memory _symbol, string memory baseURI, string memory contractURI, uint salt) external returns (address) {
        BeaconProxy proxyAddress = new BeaconProxy(beacon, getData(_name,  _symbol,  baseURI,  contractURI));
        emit ERC1155OpenGlipProxy(address(proxyAddress), to);
        return address(proxyAddress);
    }

    function createToken(address to, string memory _name, string memory _symbol, string memory baseURI, string memory contractURI, uint salt) external returns (address) {
        address beaconProxy = deployProxy(getData(_name,  _symbol,  baseURI,  contractURI), salt);
        ERC1155OpenGlip token = ERC1155OpenGlip(address(beaconProxy));
        if (to != _msgSender()) token.transferOwnership(to); else token.transferOwnership(_msgSender());
        emit ERC1155OpenGlipProxy(beaconProxy, token.owner());
        return beaconProxy;
    }

    //deploying BeaconProxy contract with create2
    function deployProxy(bytes memory data, uint salt) internal returns(address proxy){
        bytes memory bytecode = getCreationBytecode(data);
        assembly {
            proxy := create2(0, add(bytecode, 0x20), mload(bytecode), salt)
            if iszero(extcodesize(proxy)) {
                revert(0, 0)
            }
        }
    }

    //adding constructor arguments to BeaconProxy bytecode
    function getCreationBytecode(bytes memory _data) internal view returns (bytes memory) {
        return abi.encodePacked(type(BeaconProxy).creationCode, abi.encode(beacon, _data));
    }

    //returns address that contract with such arguments will be deployed on
    function getAddress(string memory _name, string memory _symbol, string memory baseURI, string memory contractURI, uint salt)
        public
        view
        returns (address)
    {   
        bytes memory bytecode = getCreationBytecode(getData(_name,  _symbol,  baseURI,  contractURI));

        bytes32 hash = keccak256(
            abi.encodePacked(bytes1(0xff), address(this), salt, keccak256(bytecode))
        );

        return address(uint160(uint(hash)));
    }

    function getData(string memory _name, string memory _symbol, string memory baseURI, string memory contractURI) view internal returns(bytes memory){
        return abi.encodeWithSelector(ERC1155OpenGlip(0).__ERC1155OpenGlip_init.selector,_name, _symbol, baseURI, contractURI, exchangeProxy, defaultMinter, forwarder);
    }


}
"},"contracts/tokens/@rarible/royalties/contracts/LibPart.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity >=0.6.2 <0.8.0;

library LibPart {
    bytes32 public constant TYPE_HASH = keccak256("Part(address account,uint96 value)");
    bytes32 public constant ARRAY_TYPE_HASH = keccak256("Parts(Part[] parts)Part(address account,uint96 value)");

    struct Part {
        address payable account;
        uint96 value;
    }

    function hash(Part memory part) internal pure returns (bytes32) {
        return keccak256(abi.encode(TYPE_HASH, part.account, part.value));
    }

    function hashParts(Part[] memory parts) internal pure returns (bytes32) {
        bytes32[] memory partsBytes = new bytes32[](parts.length);
        for (uint256 i = 0; i < parts.length; i++) {
            partsBytes[i] = LibPart.hash(parts[i]);
        }
        return keccak256(abi.encode(ARRAY_TYPE_HASH, keccak256(abi.encodePacked(partsBytes))));
    }

}
"},"contracts/tokens/@rarible/royalties/contracts/impl/AbstractRoyalties.sol":{"content":"// SPDX-License-Identifier: MIT
pragma abicoder v2;
pragma solidity >=0.6.2 <0.8.0;
import "../LibPart.sol";
abstract contract AbstractRoyalties {

    event SaveRoyaltySplitter(uint256 id, bytes32 royaltySplitterBytes);
    event SaveCreatorSignedRoyalty(uint256 id, LibPart.Part royalty);

    mapping (uint256 => LibPart.Part) creatorSignedRoyalty;
    mapping (uint256 => bytes32) royaltySplitterBytesMap;

    function _saveRoyalties(uint256 id, bytes32 royaltySplitterBytes ) internal {
        royaltySplitterBytesMap[id] = royaltySplitterBytes;
        emit SaveRoyaltySplitter(id, royaltySplitterBytes);
    }

    function _saveCreatorSignedRoyalties(uint256 id, address creator, LibPart.Part memory royalty ) internal {

        emit SaveCreatorSignedRoyalty(id, royalty);
        if (royalty.account == creator && creator == address(id >> 96)) {
            // To save gas don't set the account as it can be recovered
            royalty.account = address(0x0);
        }
        creatorSignedRoyalty[id] = royalty;

    }

    function _onRoyaltiesSet(uint256 id, address wallet, LibPart.Part[] memory _royalties) virtual internal;
}
"},"contracts/tokens/@rarible/royalties/contracts/Royalties.sol":{"content":"// SPDX-License-Identifier: MIT

